package ex01_variable;
//https://github.com/koreaitlecture
public class Ex01_literal {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println(Integer.MAX_VALUE);
		System.out.println(Integer.MIN_VALUE);
		System.out.println(2147483648L); // int 범위는 그냥쓰고 벗어나면 접미사(L)
		//정수 표현법 :그냥 입력
		//실수 포현법 : 그냥 입력
		System.out.println(Double.MAX_VALUE);
		//1.7976931348623157E308 = 1.7976931348623157 곱하기 10의 308제곱
		//4.9E-324 = 4.9 곱하기 10의 -324제곱
		
		
		System.out.println(Double.MIN_VALUE);
		//문자 표현법 : ' '
		System.out.println('a');
		System.out.println('1');
		
		
		//믄지 열 : " "
		System.out.println("123");
		//논리 표현법 : true 또는 false

		
	}

}
