package ex01_branch;

public class Ex02_switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int choice = 1;
		
		switch (choice) {
		case 1:
			System.out.println("1이다");
			break;

		case 2:
			System.out.println("2이다");
			break;
			
		default:
			System.out.println("1,2가 아니다");
			break;
		}
	}

}
